package typeofprints;

public class Main {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		float number = 8.5f;

		System.out.print("You number is " + number);
		System.out.println(" -------------------");
		
		System.out.println("You number is " + number);
		System.out.println(" -------------------");
		
		System.out.printf("You number is %.2f", number);
		System.out.println(" -------------------");
		
		System.out.format("You number is %.2f", number);
		System.out.println(" -------------------");

	}

}
